using System.IO;

private string[,] Data;
private int len;
private int wid;

private void Read_file() {
  string filePlace = "hid.csv"; // the file to read
  string cut = ";"; // the char used in splitting

  string[] adat = File.ReadAllLines(filePlace);
  len = adat.Length; // the length
  wid = adat[0].Split(cut).Length; // the width
  Data = new string[len, wid]; // len*wid=matrix
  StreamReader fireReader = new StreamReader(filePlace);
  int n = 0;
  while (!fireReader.EndOfStream) {
  string[]line = fireReader.ReadLine().Split(cut);
  for (int i = 0; i < wid; i++)
  Data[n, i] =line[i];
  n++;
  }
  fireReader.Close();
}

