using MySql.Data.MySqlClient;

namespace MySQLConnectionExample
  {
	class Program
	{
	  static void Main(string[] args)
	  {
	  Console.WriteLine("Add meg a földrészt, és megmutatja mely országok tartalmazza");
	  string foldresz=Console.ReadLine();
	  // MySQL adatbázis kapcsolat string
	  string connectionString = "Server=localhost;Database=foldrajz;Uid=root;Pwd=;";
	  // ez hozza létre a kapcsolatot az adatbázissal
		// Kapcsolat létrehozása
		using (MySqlConnection connection = new MySqlConnection(connectionString))
		{
		  try
		  {
			connection.Open();
		  //  Console.WriteLine("Sikeres kapcsolat az adatbázishoz!");
		  // SQL lekérdezés példa
		  string query = "SELECT `orszag`, `fovaros`, `terulet`, `nepesseg` FROM `orszagok` WHERE foldr_hely LIKE @foldresz ORDER BY orszag";
			MySqlCommand command = new MySqlCommand(query, connection);
			command.Parameters.AddWithValue("@foldresz", "%" + foldresz + "%");
			// itt paraméterezve a lekérdezésem
			// Adatok lekérdezése
			using (MySqlDataReader reader = command.ExecuteReader())
			{
			  while (reader.Read())
			  {   // ami a szelektbe van azokat kapcsosba
				Console.WriteLine($"{reader["orszag"]} Fővárosa: {reader["fovaros"]} Területe: {reader["terulet"]} Népessége: {reader["nepesseg"]} ");
			  }
			}
		  }
		  catch (Exception ex)
		  {
			Console.WriteLine($"Hiba történt: {ex.Message}");
		  }
		}
	  Console.ReadLine();
	}
	}
  }

function CopyTextToClipboard(id) {
  // Check if the Clipboard API is available
  if (navigator.clipboard) {
	let textToCopy = document.getElementById(id).innerText;
	if (!textToCopy)
	  console.log('Copy Error', 'Failed to copy:', "Nothing to copy");
	else {
	  navigator.clipboard.writeText(textToCopy)
		.then(() => {
		  console.log('Copy Success', 'Code copied to clipboard!');
		})
		.catch((error) => {
		  console.log('Copy Error', 'Failed to copy:', error);
		});
	}
  } else {
	console.log('Browser Error', 'Failed to copy:', 'Your browser does not support the Clipboard API.
(Or the web hosting server)');
  }
}

